package mods.minecessity;import java.util.Random;import net.minecraft.block.BlockContainer;import net.minecraft.block.material.Material;import net.minecraft.entity.player.EntityPlayer;import net.minecraft.src.ModLoader;import net.minecraft.tileentity.TileEntity;import net.minecraft.world.World;public class Y_BlockParticle extends BlockContainer{    protected Y_BlockParticle(int i)    {		super(i,Material.ground);		setTickRandomly(true);    }		public int tickRate()    {        return 1;    }	public void onNeighborBlockChange(World world, int i, int j, int k, int l)    {		if(powered(world,i,j,k)) shootParticles(world,i,j,k);    }		public void randomDisplayTick(World world, int i, int j, int k, Random random)    {		if(powered(world,i,j,k)) shootParticles(world,i,j,k);    }		public void updateTick(World world, int i, int j, int k, Random random)    {		if(powered(world,i,j,k)) shootParticles(world,i,j,k);    }		public boolean powered(World world,int i,int j,int k)	{		return world.isBlockGettingPowered(i,j,k) || world.isBlockIndirectlyGettingPowered(i,j,k);	}		public void shootParticles(World world, int i,int j,int k)	{		Y_TileEntityParticleBlock tileparticle = (Y_TileEntityParticleBlock)world.getBlockTileEntity(i, j, k);		tileparticle.shootParticles(world,i,j,k);	}		public boolean blockActivated(World world, int i, int j, int k, EntityPlayer entityplayer)    {        if(world.isRemote)        {            return true;        } else        {            Y_TileEntityParticleBlock tileparticle = (Y_TileEntityParticleBlock)world.getBlockTileEntity(i, j, k);			ModLoader.openGUI(entityplayer, new Y_GuiParticleBlock(tileparticle));            return true;        }    }		protected TileEntity getBlockEntity()    {        return new Y_TileEntityParticleBlock();    }}