package mods.minecessity.blocks;import java.util.Random;import mods.minecessity.Minecessity;import mods.minecessity.TileEntityParticleBlock;import net.minecraft.block.BlockContainer;import net.minecraft.block.material.Material;import net.minecraft.creativetab.CreativeTabs;import net.minecraft.entity.player.EntityPlayer;import net.minecraft.tileentity.TileEntity;import net.minecraft.world.World;public class BlockParticle extends BlockContainer{    public BlockParticle(int i)    {		super(i,Material.ground);		setTickRandomly(true);		setCreativeTab(CreativeTabs.tabRedstone);    }    @Override	public int tickRate(World world)    {        return 1;    }    @Override	public void onNeighborBlockChange(World world, int i, int j, int k, int l)    {		if(powered(world,i,j,k)) 			shootParticles(world,i,j,k);    }    @Override	public void updateTick(World world, int i, int j, int k, Random random)    {		if(powered(world,i,j,k))			shootParticles(world,i,j,k);    }		public boolean powered(World world,int i,int j,int k)	{		return world.getBlockPowerInput(i,j,k)!=0 || world.isBlockIndirectlyGettingPowered(i,j,k);	}		public void shootParticles(World world, int i,int j,int k)	{		TileEntityParticleBlock tileparticle = (TileEntityParticleBlock)world.getBlockTileEntity(i, j, k);		if(tileparticle!=null)			tileparticle.shootParticles(world,i,j,k);	}	@Override	public boolean onBlockActivated(World world, int i, int j, int k, EntityPlayer entityplayer, int par6, float par7, float par8, float par9)    {          entityplayer.openGui(Minecessity.instance,Minecessity.proxy.PARTICLE_GUI,world,i,j,k);        return true;    }	@Override	public TileEntity createNewTileEntity(World world)    {        return new TileEntityParticleBlock();    }}